package com.example.validators;import javax.validation.Valid;import javax.validation.Validation;import javax.validation.Validator;import javax.validation.ValidatorFactory;import com.example.entities.MemberWithValidation;import org.springframework.data.rest.core.annotation.HandleAfterCreate;import org.springframework.data.rest.core.annotation.HandleBeforeCreate;import org.springframework.data.rest.core.annotation.RepositoryEventHandler;import org.springframework.stereotype.Component;import org.springframework.validation.annotation.Validated;/** * Created by rahul.sharma on 4/21/16. *///@Component//@RepositoryEventHandler//(MemberWithValidation.class)public class MemberEventHandler {    ValidatorFactory validatorFactory = Validation.buildDefaultValidatorFactory();    Validator validator = validatorFactory.getValidator();    @HandleBeforeCreate    public void handleBeforeCreates(@Valid MemberWithValidation memberWithValidation) {        System.out.println(validator.validate(memberWithValidation));        System.out.println("=======================");    }    @HandleAfterCreate    public void handleAfterCreates(MemberWithValidation memberWithValidation) {        System.out.println("=======================");    }}